/**
intervalz.js by JR Richet
https://github.com/catbadger/intervalz
**/

let rest=20;
let dur=60;
let workset=true;
let curcount=dur;
let setcount=0;  
let elapsed=0;
let interval_count=0;
let current_set="FRESH";
let onswitch = false;
//this is the beep noise in base64
let noise = new Audio("data:audio/wav;base64,");  

//array of interval sets.
//directions for each set should go in this array
let works=[
   "GO",
   "GO",
   "GO",
   "GO",
   "GO",
   "GO",
   "GO",
   "GO",
   "GO",
   "GO"
];

$(document).ready(()=>{
    writeTime(0);
    $('#message').html("<div class='new'>"+works[setcount]+"</div>");
    $('#intervals_tot').html( works.length );
    $('#elapsed_tot').html( formatTime((works.length*dur)+((works.length-1)*rest)) );

    $('#playbtn').click(()=>{
        if ( !onswitch ) {
            onswitch=true;     
            $('#playbtn').html('<i class="material-icons">pause_circle_outline</i>');
            writeSet(works[setcount]);  
            $('#intervals_num').html( setcount+1 );                 
            work();
        } else {
            onswitch=false;
            $('#playbtn').html('<i class="material-icons">play_circle_outline</i>');
            writeSet("PAUSED");  
        }
    }); 

    $('#resetbtn').click((e)=>{
        e.preventDefault();   
        workset=true;             
        curcount=dur;
        setcount=0;
        elapsed=0;
        interval_count=0;
        current_set="FRESH";    
        onswitch=false;
        writeTime(0);
        return false;
    });
}); 

writeSet=(msg)=>{
    $( '#historybox' ).find('.new').removeClass('new').addClass('old');
    $( '#historybox' ).append( $("<div class='new'>"+msg+"</div>") );    
    $( '#directions' ).html(msg);                
}

formatTime=(s)=>{
    let min = Math.floor(s / 60);
    s = s - (min*60);
    min = min.toString().padStart(2, '0')
    s = s.toString().padStart(2, '0')
    return min+":"+s;
}

writeTime=(secs)=>{
    $('#counter').html( formatTime(secs) );
}

beep=(num=1)=>{ 
    setTimeout(()=>{
        noise.play();
        if (num>1) {
            num--;
            beep(num);
        }
    }, 300);
}

work=()=>{
    if( curcount>0 ){
        setTimeout( () => {
            if ( onswitch ){
                elapsed++;
                curcount--;
                $('#elapsed_num').html(formatTime(elapsed));
                writeTime(curcount);
                work();
            }
        }, 1000 );
    } else if( typeof(works[setcount+1])!="undefined" ){ 
        if (workset) {
            beep(2);
            workset = false;
            curcount = rest;
            writeTime(curcount);
            writeSet("REST");
            $('#uxtable,#uxtable *').css('background','#54a5be')
            work();
        } else {
            beep();
            workset = true;
            setcount++;
            curcount = dur;
            writeTime(curcount);
            $('#intervals_num').html( setcount+1 );
            writeSet(works[setcount]);
            $('#uxtable,#uxtable *').css('background','#54be54')
            work();
        }
    } else {
        beep(3);
        writeSet("DONE");    
    }
}